import './User.scss';
import { connect } from 'react-redux';
import React from 'react';
import { updateUser } from '../../store/actions/user';
import UpdateDetails from './UpdateDetails';
import SiteManagerTable from './SiteManagerTable';
import AuctionManagerTable from './AuctionManagerTable';
import UserTable from './UserTable';

import { BrowserRouter as Router, Link, useRouteMatch, Route, Switch } from 'react-router-dom'

const YourProfile = (props) => {

    return (
        <>

            {/* כפתור למעבר לעריכת פרטים אישיים */}
            <Link to={'/your_profile/update_your_details'}><button>Update your details</button></Link>

            <Router>
                <Switch>
                    <Route path="/your_profile/update_your_details"><UpdateDetails /></Route>
                    <Route path="/your_profile"><UpdateDetails />
                        {/* כאן תוצג טבלה בהתאם לסטטוס של המשתמש */}
                        {props.currentUser.status == 'USER' ? <UserTable /> :
                            props.currentUser.status == 'AUCTION_MANAGER' ? <AuctionManagerTable /> : <SiteManagerTable />}
                    </Route>
                </Switch>
            </Router>
        </>
    );

}

const mapStateToProps = (state) => {
    return {
        // currentUser: state.user.currentUser
    };
}
export default connect(mapStateToProps, { updateUser })(YourProfile);
