import p from '../../img/profile.png';
import React, { useState } from 'react';
import { signIn } from '../../store/actions/signIn';
import { connect } from 'react-redux';
import "./User.scss"
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import FilledInput from '@material-ui/core/FilledInput';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import Button from '@material-ui/core/Button';
import InputAdornment from '@material-ui/core/InputAdornment';
import IconButton from '@material-ui/core/IconButton';
import LoginGoogle from './LoginGoogle';
import { useForm } from "react-hook-form";

import { setCurrentUser } from '../../store/actions/signUp';
const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    width: '100%'
  },
  margin: {
    margin: theme.spacing(1),
  },
  withoutLabel: {
    marginTop: theme.spacing(3),
  },
  textField: {
    width: '25ch',
  },
  input_pas_ma: {
    color: '#8e8e95',
    height: '4vh !important',
    background: 'none !important',
    color: '#8e8e95 !important',
    width: '70% !important',
    padding: '0vw !important',
    borderBottomColor: '8e8e95 !important',
    background: 'none !important',
    alignSelf: 'flex-end',
    marginRight: 'auto',
    marginLeft: 'auto'
  },
  eye: {
    color: '#8e8e95',

  }
}));

const SignIn = (props) => {
  const [values, setValues] = React.useState({
    amount: '',
    password: '',
    weight: '',
    weightRange: '',
    showPassword: false,
  });

  const handleChange = (prop) => (event) => { setValues({ ...values, [prop]: event.target.value }); };

  const handleClickShowPassword = () => { setValues({ ...values, showPassword: !values.showPassword }); };

  const handleMouseDownPassword = (event) => { event.preventDefault(); };

  let password = "";
  let email = "";

  let submit = (data, e) => {
    e.preventDefault();

    password = data.password;
    email = data.email;
    props.signIn(password, email)

  }
  const { register, handleSubmit, formState: { errors } } = useForm();

  const classes = useStyles();

  return (
    <center>
      <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit(submit)} /*onLoad={() => { document.getElementById('emailInput').focus()}}*/>

        <img className='profile_img' src={p} />

        <div className={"inputs_btns"}>
          <FilledInput
            id="emailInput"
            onKeyPress={e => { if (e.key === 'Enter') document.getElementById('passInput').focus() }}
            type={'text'}
            placeholder="Email"
            className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
            variant="filled"
            // onChange={(e) => { setEmail(e.target.value) }}
            startAdornment={
              <InputAdornment position="start">
                <i className="envelope icon"></i>
              </InputAdornment>
            }
            {...register('email', { required: true })}

          />
          <FilledInput
            id="passInput"
            type={values.showPassword ? 'text' : 'password'}
            onChange={handleChange('password')}
            onKeyPress={e => { if (e.key === 'Enter') document.getElementById('btnSubmit').click() }}
            placeholder="Password"
            className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
            variant="filled"
            startAdornment={
              <InputAdornment position="start">
                <i className="lock icon"></i>
              </InputAdornment>
            }
            endAdornment={
              <InputAdornment position="end">

                <IconButton
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  className={classes.eye}
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
            {...register('password', { required: true })}

          />
        </div>
        <Button type="submit" variant="contained" className={"login_btn_sign_in"} id="btnSubmit">Login</Button>

        {/* {password == "" ? <h2 id="forgot">Forgot Password?</h2> : null} */}
        <br />

        {/* <LoginGoogle /> */}

      </form>
    </center >
  );
}
const mapStateToProps = (state) => {
  return {
  };
}
export default connect(mapStateToProps, { signIn, setCurrentUser })(SignIn);
