import { connect } from "react-redux";
import ProductList from './ProductList';
import React, { useEffect } from 'react';
import { Link } from 'react-router-dom'
import Timer from "./Timer";
import './Auction.scss';
import { setUserByStorage, setCurrentAuctionByStorage, setNewAuctionByStorage } from "../../store/actions/user";

const CurrentAuction = (props) => {
    useEffect(() => {
        let id = localStorage.getItem("user");

        if (id && props.currentUser == null) {

            let a_id = localStorage.getItem("currentAuction");
            // let n_a_id = localStorage.getItem("newAuction");
            if (a_id) props.setCurrentAuctionByStorage(a_id);
            //     if (n_a_id) props.setNewAuctionByStorage(n_a_id);
            props.setUserByStorage(id);
        }

    }, [])
    return (<>
        <center>
            <Link to={`/auction/about`}>
                <img src={props.currentAuction.logo} style={{width:'2vw',height:'auto',display:'inline-block'}}/>
                <p>About {props.currentAuction.organizationName || props.currentAuction.name}</p>
            </Link>
        </center>

        <ProductList />

    </>);
}
const mapStateToProps = (state) => {
    return {
        currentUser: state.user.currentUser,
        currentAuction: state.currentAuction.currentAuction

    }
}

export default connect(mapStateToProps, { setNewAuctionByStorage, setCurrentAuctionByStorage, setUserByStorage })(CurrentAuction);
