import { connect } from "react-redux";
import { Link } from 'react-router-dom';
import './home.scss';
import { setLogin } from '../../store/actions/home';
import { useEffect } from "react";
import { updateCurrentUser } from '../../store/actions/user'


const HomeFooter = (props) => {
    return (
        <footer className="home_footer">
            <div id="logo_in_home_footer" ></div>
            <div id="contact_us"><p>CONTACT US</p></div>
            <div id="menu_footer">
                <p href={"/home"} className="menu_footer_link">HOME</p>
                <p onClick={() => window.scrollTo(0, 900)} href={"/home"} className="menu_footer_link">AUCTIONS</p>
                <p onClick={props.currentUser ? null : () => { window.scrollTo(0, 0); props.setLogin(true) }} href={props.currentUser ? "/new_auction" : '#'} className="menu_footer_link">BUILDING</p>
                <p href={"/about"} className="menu_footer_link">ABOUT</p>
            </div>
            <div id="text_footer">
                <p>  Building Chinese auctions,<br />
                    And a database of all sales built.<br />
                    The site is used by the masses<br />
                    And is gaining momentum among the general public.<br />
                    The site has gained worldwide publicity<br />
                    Thanks to its uniqueness.
                </p>
            </div>
        </footer>
    );
}
const mapStateToProps = (state) => {
    return {
        currentUser: state.user.currentUser
    }
}

export default connect(mapStateToProps, { setLogin, updateCurrentUser })(HomeFooter);
