import React from 'react';
import { connect } from 'react-redux';
import moment from 'moment'
import Button from '@material-ui/core/Button';
import './yourProfile.scss'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import { Link } from 'react-router-dom';

import { getAuctionsArray } from '../../store/actions/user'
import DeleteModal from './DeleteModal'
import ApprovalModal from './ApprovalModal'
import { setDeleteAuctionModal } from '../../store/actions/user'
import { setApprovalAuctionModal } from '../../store/actions/user'
import { setDisApprovalAuctionModal } from '../../store/actions/user'
import { approvalAuction } from '../../store/actions/user'
import { setSelectedAuctionToOptions } from '../../store/actions/user'
import { isAuctionApproved } from '../../store/actions/user'
import IconButton from '@material-ui/core/IconButton';
import edit from '../../img/icons/edit-file.png'
import approval from '../../img/icons/thumbs-up.png'
import disapproval from '../../img/icons/thumb-down.png'
import st from '../../img/icons/stadistics.png'
import results from '../../img/icons/results.png'
import de from '../../img/icons/dustbin.png'

const useStyles = makeStyles({
    root: {
        width: '80%',
    },
    container: {
        maxHeight: 440,
    }
});


const AuctionManagerTable = (props) => {
    const columns = [
        { id: 'name', label: 'Chinese auction Name', minWidth: 170 },
        {
            id: 'start_date',
            label: 'Start Date',
            minWidth: 170,
            align: 'right',
            // format: (value) => moment(value).format('D/MM/YYYY')
        },
        {
            id: 'end_date',
            label: 'Lotteries date',
            minWidth: 170,
            align: 'right',
            //  format: (value) =>moment(value).format('D/MM/YYYY')
        },
        {
            id: 'done',
            label: 'Done',
            minWidth: 170,
            align: 'right',
            format: (value) => value.toFixed(2),
        },
        {
            id: 'options',
            label: 'Options',
            minWidth: 170,
            align: 'right',
            format: (value) => value.toFixed(2),
        }
    ];
    const deleteAuction = (_id) => {
        props.setDeleteAuctionModal(true);
        props.setSelectedAuctionToOptions(_id);
    }
    const lotteryApproval = (_id) => {
        props.setSelectedAuctionToOptions(_id);
        if (!props.isAuctionApproved(_id)) props.setApprovalAuctionModal(true);
        else props.setDisApprovalAuctionModal(true);

    }

    function createData(name, start_date, end_date, done, _id) {
        // let isApproved=props.isAuctionApproved(_id);
        let isApproved = Math.random() < 0.5; //לבינתיים

        let options = <div className="optionsBtn">

            {isApproved ?
                <IconButton onClick={() => lotteryApproval(_id)} title="disapproval lottery" aria-label="disapproval"><img className="my_icon" src={disapproval} ></img></IconButton>
                : <IconButton onClick={() => lotteryApproval(_id)} title="approval lottery" aria-label="approval"><img className="my_icon" src={approval} ></img></IconButton>
            }
            <Link onClick={() => props.setSelectedAuctionToOptions(_id)} to={`/your_profile/edit_auction`}><IconButton title="edit" aria-label="edit"><img className="my_icon" src={edit} ></img></IconButton></Link>
            <Link onClick={() => props.setSelectedAuctionToOptions(_id)} to={`/your_profile/results`}><IconButton title="results" aria-label="results"><img className="my_icon" src={results} ></img></IconButton></Link>
            <Link onClick={() => props.setSelectedAuctionToOptions(_id)} to={`/your_profile/statistics`}><IconButton title="statistics" aria-label="statistics"><img className="my_icon" src={st} ></img></IconButton></Link>
            <IconButton onClick={() => deleteAuction(_id)} title="delete" aria-label="delete"><img className="my_icon" src={de} ></img></IconButton>
        </div>

        return { name, start_date, end_date, done, options };
    }

    const rows = [
        // TODO: למה צריך לעשות כאן את המומנט
        createData('לזכות ברגע', moment(new Date(2021, 7, 1)).format('D/MM/YYYY'), moment(new Date(2021, 9, 1)).format('D/MM/YYYY'), 'false', 231321312),
        createData('הרבה נחת', moment(new Date(2021, 8, 1)).format('D/MM/YYYY'), moment(new Date(2021, 10, 1)).format('D/MM/YYYY'), 'false', 3123123132),
        createData('ועל גמילות חסדים', moment(new Date(2020, 10, 1)).format('D/MM/YYYY'), moment(new Date(2020, 12, 1)).format('D/MM/YYYY'), 'true', 435435524)
    ];

    // TODO: sort by date
    /*const rows = props.getAuctionsArray(props.currentUser).map((item) => {
        return createData(item.name, item.registrationStartDate, item.lotteriesDate, item.status);
    });*/


    const classes = useStyles();
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

    /*const handleChangePage = (event, newPage) => {
                setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
                setRowsPerPage(+event.target.value);
            setPage(0);
    };*/

    return (
        <center>
            {props.show_delete ? <DeleteModal /> : null}
            {props.show_approval ? <ApprovalModal /> : null}
            <h1>Chinese Auction Manager</h1>
            <Paper className={classes.root}>
                <TableContainer className={classes.container}>
                    <Table stickyHeader aria-label="sticky table">
                        <TableHead className='auctionManagerTableCls'>
                            <TableRow>
                                {columns.map((column) => (
                                    <TableCell
                                        key={column.id}
                                        align={column.align}
                                        style={{ minWidth: column.minWidth }}
                                    >
                                        {column.label}
                                    </TableCell>
                                ))}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                                return (
                                    <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                                        {columns.map((column) => {
                                            const value = row[column.id];
                                            return (
                                                <TableCell key={column.id} align={column.align}>
                                                    {column.format && typeof value === 'number' ? column.format(value) : value}
                                                </TableCell>
                                            );
                                        })}
                                    </TableRow>
                                );
                            })}
                        </TableBody>
                    </Table>
                </TableContainer>

            </Paper>

        </center>);
}
const mapStateToProps = (state) => {
    return {
        currentUser: state.user.currentUser,
        show_delete: state.user.deleteAuctionModal,
        show_approval: state.user.approvalAuctionModal,
    };
}
export default connect(mapStateToProps, { setDeleteAuctionModal, setSelectedAuctionToOptions, setDisApprovalAuctionModal, setApprovalAuctionModal, approvalAuction, getAuctionsArray, isAuctionApproved })(AuctionManagerTable);
