import axios from 'axios';

import { getAuctionByIdFromDB } from './auctionUtils';
import { setNewAuction } from '../store/actions/newAuction';
import { signIn,loginGoogle } from '../store/actions/signIn';
import { setCurrentAuction } from '../store/actions/currentAuction';

export const addUser = (user) => {
    return axios.post("http://localhost:5000/users", user)
}


export const sendContact = async (details) => {
    return axios.post(`http://localhost:5000/users/sendContactToSiteManager`, details).then(succ => {
        console.log(succ.data)
    })
    //TODO: dispatch ???
}

export const getUserOrdersListFromDB = async (_id) => {
    return axios.get(`http://localhost:5000/orders/userOrdersList/${_id}`)
}


export const LoginFromStorage = () => {
    if (localStorage.getItem("login") == "true")
        signIn(localStorage.getItem("pass"), localStorage.getItem("email"));
    else if (localStorage.getItem("login") == "google")
        loginGoogle(localStorage.getItem("name"), localStorage.getItem("email"));
}
export const GetDataFromStorage = () => {
    var newAuctionId = localStorage.getItem("newAuction");
    var currentAuctionId = localStorage.getItem("currentAuction");
    if (newAuctionId && newAuctionId.length > 1)
        getAuctionByIdFromDB(newAuctionId).then(succ => {
            if (succ.status != 400) setNewAuction(succ.data);
        })
    if (currentAuctionId && currentAuctionId.length > 1)
        getAuctionByIdFromDB(currentAuctionId).then(succ => {
            if (succ.status != 400) setCurrentAuction(succ.data);
        })
}
