import './auctionManager.scss'

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { connect } from "react-redux";
import React, { useEffect, useState, useRef } from 'react';
import pic from '../../../img/picture2.png'
import { getAuctionFromDB } from '../../../store/actions/currentAuction'
import { Link } from 'react-router-dom'
import { getBestSellingProductInAuctionFromDB } from '../../../utils/auctionManagerUtils'
import { getTotalRevenueFromDB } from '../../../utils/auctionManagerUtils'
const AuctionStatistics = (props) => {

    useEffect(() => {
        if (props.auction) {
            getBestSellingProductInAuctionFromDB(props.auction._id).then(succ => {
                setBestProduct(succ.data);
                console.log(succ.data)
            });
            getTotalRevenueFromDB(props.auction._id).then(succ => {
                if (succ.status != 400) setSum(succ.data);
                console.log(succ.data)
            })
        }
        else window.location = "http://localhost:3000/your_profile"
    }, []);

    const [bestProduct, setBestProduct] = useState(null);
    const [sum, setSum] = useState(0);


    return (
        <>

            <h1 >Chinese auction statistics</h1>


            {props.auction &&
                <>
                    <Card sx={{ maxWidth: 345 }}>
                        <CardMedia
                            component="img"
                            height="140"
                            image={props.auction.image || pic}
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="div">Total revenue</Typography>
                            <Typography variant="body2" color="text.secondary">{sum}</Typography>
                        </CardContent>
                    </Card>




                    <Card sx={{ maxWidth: 345 }}>
                        <CardMedia
                            component="img"
                            height="140"
                            image={bestProduct.image || pic}
                        />
                        <CardContent>
                            <Typography gutterBottom variant="h5" component="div">Best selling product</Typography>
                            <Typography variant="body2" color="text.secondary">{bestProduct.sum}</Typography>
                        </CardContent>
                    </Card>


                </>}

        </>
    );

}

const mapStateToProps = (state) => {
    return {
        auction: state.auctionManager.selected_auction_to_options

    };
}
export default connect(mapStateToProps, {})(AuctionStatistics);
