import React from "react";
import { MDBRow, MDBCol, MDBCard, MDBCardBody, MDBIcon, MDBBtn, MDBInput } from "mdbreact";

const ContactForm = () => {

    const send = (e) => {
        let details = { name: e.target.from_name.value, email: e.target.from_email.value, message: e.target.message.value, subject: e.target.subject.value }
        sendContact(details);
    }
    return (
        <section className="contact-section my-5">

            <MDBCard>
                <MDBRow>
                    <MDBCol lg="8">
                        <MDBCardBody className="form">
                            <h3 className="mt-4">
                                <MDBIcon icon="envelope" className="pr-2" />
                                Write to us:
                            </h3>

                            <MDBRow>
                                <MDBCol md="6">
                                    <div className="md-form mb-0">
                                        <MDBInput
                                            type="text"
                                            id="form-contact-name"
                                            label="Your name"
                                        />
                                    </div>
                                </MDBCol>
                                <MDBCol md="6">
                                    <div className="md-form mb-0">
                                        <MDBInput
                                            type="text"
                                            id="form-contact-email"
                                            label="Your email"
                                        />
                                    </div>
                                </MDBCol>
                            </MDBRow>

                            <MDBRow>
                                <MDBCol md="6">
                                    <div className="md-form mb-0">
                                        <MDBInput
                                            type="text"
                                            id="form-contact-phone"
                                            label="Your phone"
                                        />
                                    </div>
                                </MDBCol>
                                <MDBCol md="6">
                                    <div className="md-form mb-0">
                                        <MDBInput
                                            type="text"
                                            id="form-contact-company"
                                            label="Your company"
                                        />
                                    </div>
                                </MDBCol>
                            </MDBRow>





                            <MDBRow>
                                <MDBCol md="12">
                                    <div className="md-form mb-0">
                                        <MDBInput
                                            type="textarea"
                                            id="form-contact-message"
                                            label="Your message"
                                        />
                                        {/* <MDBBtn rounded color="blue">
                                            <MDBIcon icon="paper-plane" />
                                        </MDBBtn> */}
                                    </div>
                                </MDBCol>
                            </MDBRow>

                        </MDBCardBody>
                    </MDBCol>
                    <MDBCol lg="4">
                        <MDBCardBody className="contact text-center h-100 white-text">
                            <h3 className="my-4 pb-2">Contact information</h3>
                            <ul className="text-lg-left list-unstyled ml-4">
                                <li>
                                    <p>
                                        <MDBIcon icon="map-marker-alt" className="pr-2" />
                                        New York, 94126 USA
                                    </p>
                                </li>
                                <li>
                                    <p>
                                        <MDBIcon icon="phone" className="pr-2" />+ 01 234 567 89
                                    </p>
                                </li>
                                <li>
                                    <p>
                                        <MDBIcon icon="envelope" className="pr-2" />
                                        contact@example.com
                                    </p>
                                </li>
                            </ul>
                            <hr className="hr-light my-4" />

                        </MDBCardBody>
                    </MDBCol>
                </MDBRow>
                <Button type='submit' onClick={send} color='green'>Submit</Button>
            </MDBCard>
        </section>
    );
}

export default ContactForm;
