import { Link } from 'react-router-dom'
import './CartAll.scss';
import { connect } from "react-redux";
import React, { useEffect, useState } from 'react';
import { emptyTheCartByAuction, getCartFromDB } from '../../utils/userUtils';
import { getAuctionFromDB } from '../../store/actions/currentAuction'
import de from '../../img/icons/dustbin.png'
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

const CartAll = (props) => {

    useEffect(() => {
        getCartFromDB(props.user.currentUser._id).then(succ => {
            if (succ.status != 400) { props.setCartAll(succ.data); }
        })
    }, [])
    const columns = [
        { id: 'name', label: 'Name', align: 'left', minWidth: 80 },
        {
            id: 'sum',
            label: 'Amount to pay',
            minWidth: 170,
            align: 'left',
            format: (value) => value.toFixed(2),
        },
        {
            id: 'deleteCart',
            label: 'deleteCart',
            minWidth: 170,
            align: 'left',
            format: (value) => value.toFixed(2),
        },
        {
            id: 'continue',
            label: 'Continue',
            minWidth: 170,
            align: 'left',
            format: (value) => value.toFixed(2),
        }
    ];

    function createData(auctionCartObj) {

        let deleteCart = <IconButton title="Delete"
            onClick={() => {
                emptyTheCartByAuction(auctionCartObj._id, props.currentUser._id).then(succ => {
                    if (succ.status != 400) setCartAll(succ.data);
                })
            }}>
            <img className="my_icon" src={de} ></img>
        </IconButton>;

        let sum = 0;
        auctionCartObj.map(item => { sum += item.qty * item.productId.price });

        let c = <Link
            onClick={() => props.getAuctionFromDB(auctionCartObj.auctionId)}
            to={`/auction`}>
        </Link>

        return { name: auctionCartObj.name, sum, deleteCart, continue: c };
    }
    const classes = useStyles();
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    const [cartAll, setCartAll] = React.useState(null);

    return (
        <center>
            <h1>CartAll Component</h1>
            <Link to={'/auction'}>Back</Link>{/*לצאת מהסל, חזרה לכל המוצרים*/}

            <h1>Chinese Auction Manager</h1>
            <Paper className={classes.root}>
                <TableContainer className={classes.container}>
                    <Table stickyHeader aria-label="sticky table">
                        <TableHead className='auctionManagerTableCls'>
                            <TableRow>
                                {columns.map((column) => (
                                    <TableCell
                                        key={column.id}
                                        align={column.align}
                                        style={{ minWidth: column.minWidth }}
                                    >
                                        {column.label}
                                    </TableCell>
                                ))}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {cartAll && cartAll.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                                return (
                                    <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                                        {columns.map((column) => {
                                            const value = row[column.id];
                                            return (
                                                <TableCell key={column.id} align={column.align}>
                                                    {column.format && typeof value === 'number' ? column.format(value) : value}
                                                </TableCell>
                                            );
                                        })}
                                    </TableRow>
                                );
                            })}
                        </TableBody>
                    </Table>
                </TableContainer>

            </Paper>

        </center>);
}

const mapStateToProps = state => {
    return {
        user: state.user,
        currentAuction: state.currentAuction.currentAuction,
        currentUser: state.user.currentUser
    }
}
export default connect(mapStateToProps, { getAuctionFromDB })(CartAll);
