import './Auction.scss';
import { Header, Modal } from 'semantic-ui-react';
import p from '../../img/car.jpg';
import IconButton from '@material-ui/core/IconButton';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import React from 'react';
import { addProductToshoppingCartInDB, deleteProductToshoppingCartInDB, updateShoppingCart } from '../../store/actions/user';
import { connect } from "react-redux";

const useStyles = makeStyles({
  root: {
    maxWidth: 300,
  },
  media: {
    height: 160,
  },
});

const Product = (props) => {


  const [open, setOpen] = React.useState(false)
  const classes = useStyles();
  let [cnt, setCnt] = useState(0);
  let image_src = p;//עד שנעשה את הקטע של התמונות
  let description = props.product.description;
  let name = props.product.name;
  let price = props.product.prices;

  return (
    <Modal
      closeIcon
      open={open}
      trigger={
        <Card className={classes.root} >
          {name}
          <Typography gutterBottom variant="h5" component="h2">
            {price}
          </Typography>
          <CardMedia
            className={classes.media}
            image={image_src}
            title="Contemplative Reptile"
          />
          {/* הוסף לסל */}
          <IconButton color="primary" aria-label="add to shopping cart">
            <AddShoppingCartIcon
              onClick={(e) => {
                e.stopPropagation();
                props.addProductToCart(cnt, props.product);
                setCnt(0);
              }} />
          </IconButton>

          <IconButton color="primary"
            onClick={(e) => {
              e.stopPropagation();
              deleteProductToshoppingCartInDB().then(succ => {
                //חוזר כל הסל
                if (succ.status != 400) props.updateShoppingCart(succ.data)
              })
            }}  >-</IconButton>


          <IconButton color="primary"
            onClick={(e) => {
              e.stopPropagation();
              addProductToshoppingCartInDB().then(succ => {
                //חוזר כל הסל
                if (succ.status != 400) props.updateShoppingCart(succ.data)
              })
            }}
          >+ </IconButton>

        </Card>}
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
    >

      <Header ><h1>{name}</h1></Header>
      <Modal.Content>
        {description}<br />
        <img src={image_src}></img>
      </Modal.Content>
    </Modal>
  )
}

const mapStateToProps = state => {
  return {
  }
}
export default connect(mapStateToProps, { updateShoppingCart })(Product);
