import React from 'react';
import { useStorageReducer } from 'react-storage-hooks';
import { connect } from "react-redux";
import { updateUserState } from '../store/actions/user'

function ReducerCounter() {
  const [state, dispatch, writeError] = useStorageReducer(
    localStorage,//לשמור בלוקל סטורג
    'reducer-counter',//איך יקראו למשתנה בלוקל סטורג
    props.updateUserState,//איזה רדיוכר להפעיל
    // { count: 0 }//מה לשלוח לרדיוכר
  );

  return (
    <>
      <p>You clicked {state.count} times</p>
      <button onClick={() => dispatch({ type: 'inc' })}>+</button>
      <button onClick={() => dispatch({ type: 'dec' })}>-</button>
      {writeError && (
        <pre>Cannot write to localStorage: {writeError.message}</pre>
      )}
    </>
  );
}

const mapStateToProps = state => {
}
export default connect(mapStateToProps, {  updateUserState })(ReducerCounter);
