import { connect } from "react-redux";
import Auction from './Auction'
import CurrentAuction from './CurrentAuction'
import { setCurrentAuction } from '../../store/actions/currentAuction'
import { Link, BrowserRouter as Router, Route, useRouteMatch } from 'react-router-dom';

const AuctionList = (props) => {
    const { url, path } = useRouteMatch();

    return (<>
        {props.arr.map((item) => {
            return (
                <Link key={parseInt(item.code)}
                    onClick={() => props.setCurrentAuction(item.code)}
                    to={`/auction:${item.code}`}
                    component={CurrentAuction}>
                    <Auction key={parseInt(item.code)} item={item} />
                </Link>
            )
        })}
        <Switch>
            <Route path={`${path}/:id`}><CurrentAuction /></Route>
            <Route exact path={`${path}`}><p>לחץ על הקורס המבוקש</p></Route>
        </Switch>
    </>
    );
}
const mapStateToProps = (state) => {
    return {
        arr: state.main.auctionsList,
    }
}

export default connect(mapStateToProps, { setCurrentAuction })(AuctionList);


const CoursesList = () => {

    const { state } = useLocation();
    let linkList = cArr.map((item) => {
        if (item.lessonsCount >= parseInt(input.current == null ||
            input.current.value == "" ||
            input.current.value == "0" ? 0 : input.current.value))
            return <li key={item.id}>
                <Link to={`${url}/${item.id}`}>{item.name}</Link>{/*כאן שלחנו איידי לקומפוננטה שמציגה חוג אחד*/}
            </li>
        else return null;
    })
    return (
        <div>
            <p>hello to {name}</p>            <h1>Courses</h1>            <ul>{linkList}</ul>
            <input type="number" placeholder="lessons count" /*onKeyUp={byLessonsCount}*/ ref={input} onKeyUp={() => setSetInput()} /><br />
            <Switch>
                <Route path={`${path}/:id`}><CourseDetails arr={cArr} /></Route>
                <Route exact path={`${path}`}><p>לחץ על הקורס המבוקש</p></Route>
            </Switch>
        </div>
    );
}
