import React from 'react'
import './Auction.scss';
import { Header, Modal } from 'semantic-ui-react';
import p from '../../img/car.jpg';
import IconButton from '@material-ui/core/IconButton';
import { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardMedia from '@material-ui/core/CardMedia';
import Typography from '@material-ui/core/Typography';
import { connect } from "react-redux";
import { deleteProductFromCart } from '../../store/actions/user';
import Button from '@material-ui/core/Button';
import { useStorageReducer } from 'react-storage-hooks';
import { userReducer as reducer, initialState as userState } from '../../store/reducers/userState.js'
import * as actionTypes from '../../store/actionTypes'

const useStyles = makeStyles({
  root: {
    maxWidth: 300,
  },
  media: {
    height: 160,
  },
});

const getCnt = (_id) => {
 /* let index = state.shoppingCart.findIndex(item => item.product_id == _id);
  if (index != -1)
    return state.shoppingCart[index].cnt;*/
  return 0;
}

const ProductInCart = (props) => {
  const [open, setOpen] = React.useState(false)
  const classes = useStyles();

  let image_src = p;//עד שנעשה את הקטע של התמונות
  let description = props.item.product.description;
  let name = props.item.product.name;
  let price = props.item.product.prices;
  let _id = props.item.product._id;

  const [state, dispatch, writeError] = useStorageReducer(
    localStorage,
    'user',
    reducer,
    userState
  );

  return (
    <Modal
      closeIcon
      open={open}
      trigger={
        <Card className={classes.root} >
          {name}
          <Typography gutterBottom variant="h5" component="h2">
            {price}
          </Typography>
          <CardMedia
            className={classes.media}
            image={image_src}
            title="Contemplative Reptile"
          />

          <i className="trash icon" onClick={(e) => { e.stopPropagation(); dispatch({ type: actionTypes.DELETE_PRODUCT_FROM_CART, payload: _id }); }}></i>
          <IconButton color="primary"
            onClick={(e) => {
              let c = getCnt(_id);
              if (c > 0)
                dispatch({ type: actionTypes.SET_CNT_PRODUCT_IN_CART, payload: { _id: _id, cnt: c - 1 } });
              e.stopPropagation();
            }}  >
            -</IconButton>
          {/* <h2>{getCnt()}</h2>     */}
          {/* {state && <h2>{state.shoppingCart.findIndex(item => item.product_id == _id) == -1 ? null : state.shoppingCart.find(item => item.product_id == _id).cnt}</h2>} */}
          <IconButton color="primary"
            onClick={(e) => {
              let c = getCnt(_id);
              dispatch({ type: actionTypes.SET_CNT_PRODUCT_IN_CART, payload: { _id: _id, cnt: c + 1 } }); e.stopPropagation();
            }}
          >+</IconButton>
        </Card>}
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
    >

      <Header ><h1>{name}</h1></Header>
      <Modal.Content>
        {description}<br />
        <img src={image_src}></img>
      </Modal.Content>
    </Modal>
  )
}


export default connect(null, { /*deleteProductFromCart*/ })(ProductInCart);
