import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import './yourProfile.scss'
import OrderOptions from './OrderOptions';
import moment from 'moment'

const UserTable = (props) => {
    const columns = [
        {
            id: 'name',
            label: 'Name',
            minWidth: 170,
            align: 'left',

        },
        {
            id: 'orderDate',
            label: 'Order Date',
            minWidth: 100,
            align: 'left',
            //    formatDate: (date) => Moment(date).format('DD-MM-YYYY')
            // format: v => v.toLocaleDateString("en-US")

        },
        {
            id: 'sum',//
            label: 'Sum',//מה יהיה רשום
            minWidth: 170,
            align: 'left',
            format: (value) => value.toLocaleString('en-US'),
        },
        {
            id: 'options',
            label: 'Options',
            minWidth: 170,
            align: 'left',
            format: (value) => value.toLocaleString('en-US'),
        }
    ];

    const createData = (order) => {
        const options = <OrderOptions order={order} key={order._id} />;//הכפתורים
        const n = order.auctionId.organizationName + " : " + order.auctionId.name;
        let d = moment(new Date(order.orderDate)).format('D/MM/YYYY');
        let sum = order.amountToPay;
        return { name: n, orderDate: d, sum: sum, options };
    }

    const [rows, setRows] = useState([]);

    const useStyles = makeStyles({
        root: {
            width: '60%',
        },
        container: {
            maxHeight: 440,
        },
    });

    useEffect(() => {
        debugger;
        let arr = [];
        props.orders.map((o) => { arr.push(createData(o)) });
        setRows(arr);

    }, [])

    const classes = useStyles();
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);
    let arr = [];
    props.orders.map((o) => { arr.push(createData(o)) });
    setRows(arr);
    const style1 = { marginLeft: '36vw', marginTop: '4vh' }
    return (
        <Paper className={classes.root} style={style1}>
            <TableContainer className={classes.container} >
                <Table stickyHeader aria-label="sticky table">
                    <TableHead>
                        <TableRow>
                            {columns.map((column) => (
                                <TableCell
                                    key={column.id}
                                    align={column.align}
                                    style={{ minWidth: column.minWidth }}
                                >
                                    {column.label}
                                </TableCell>
                            ))}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                            return (
                                <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                                    {columns.map((column) => {

                                        const value = row[column.id];
                                        return (
                                            <TableCell key={column.id} align={column.align}>
                                                {value}
                                            </TableCell>
                                        );
                                    })}
                                </TableRow>
                            );
                        })}
                    </TableBody>
                </Table>
            </TableContainer>
        </Paper>
    );
}

const mapStateToProps = (state) => {
    return {
        user: state.user.currentUser,
        orders: state.user.ordersList
    };
}
export default connect(mapStateToProps, {})(UserTable);
