import React, { useEffect, useState } from "react";
import Popover from '@mui/material/Popover';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import { getOrderDetails } from '../../utils/orderUtils'
import { getAuctionWithWinners } from '../../utils/auctionUtils'
import { getAuctionIsDone } from '../../store/actions/auctionManager';
import IconButton from '@material-ui/core/IconButton';
import results from '../../img/icons/results.png'
import viewOrder from '../../img/icons/view-order.png'
import add from '../../img/icons/add-order.png'
import { Item } from "semantic-ui-react";
import { getInputAdornmentUtilityClass } from "@mui/material";
import OrderDetails from './OrderDetails'
export default function OrderOptions(props) {

  const [anchorEl, setAnchorEl] = React.useState(null);
  const handleClick = (event) => { setAnchorEl(event.currentTarget); };
  const handleClose = () => { setAnchorEl(null); };
  const open = Boolean(anchorEl);
  const id = open ? 'simple-popover' : undefined;

  let [productsList, setProductsList] = useState([]);
  let [details, setDetails] = useState([]);
  let [gifts, setGifts] = useState([]);
  let [a, setA] = useState([]);

  useEffect(() => {
    console.log("---------------props.order")
    console.log(props.order)
    // getAuctionWithWinners("615dd14171ffd48b48935b3a"/*props.order.auctionId*/).then(succ => {
    //   debugger;
    //   setA(succ.data);
    //   setProductsList(succ.data.productList);//רשיתמ מוצרים עם הזוכים של מכירה זו
    //   setDetails(props.order.orderDetails);//פרטי הזמנה זו
    //   setGifts(props.order.giftCodes)//מתנות של הזמנה זו

    // });
  }, []);

  return (
    <div>
      <h1>{a}</h1>
      <OrderDetails details={details} gifts={gifts} />
      <IconButton aria-describedby={id} variant="contained" onClick={handleClick} /*disabled={getAuctionIsDone() == false}*/><img title="Chiense auction results" className="table_options_icon" src={results} /></IconButton>
      <Popover
        id={id}
        open={open}
        anchorEl={anchorEl}
        onClose={handleClose}
        anchorOrigin={{
          vertical: 'bottom',
          horizontal: 'left',
        }}
      >
        <Typography sx={{ p: 2 }}>{
          getAuctionWithWinners("615dd14171ffd48b48935b3a").
            then(succ => {
              succ.data.productsList.map((p, index) => { return <p key={index}>{p.name}:{p.winnerId.confidentiality ? "Anonymous" : p.winnerId.userName}</p> }
              )
            }
            )}
        </Typography>
      </Popover>
      <IconButton><img title="Add order" className="table_options_icon" src={add} /></IconButton>
    </div>
  );
}


