import axios from 'axios';
import * as actionTypes from '../actionTypes';
import { setCurrentUser } from './signUp'
export const signIn = (password, email) => {

    // return axios.get(`http://localhost:5000/users/signIn/${password}&${email}`)

    return (dispatch) => {
        axios.get(`http://localhost:5000/users/signIn/${password}&${email}`).then(succ => {
            if (succ.status != 400) {
                dispatch(setCurrentUser(succ.data), localStorage.setItem("t_user", succ.data),console.log("ttt_sign_in"));//הגדרת המשמש הנוכחי בסטייט וגם בסטורג זמנית
            }
        })
    }
}
