import { connect } from "react-redux";
import Product from './Product'
import Cart from "./Cart";
import { setShowCart } from '../../store/actions/user'
const ProductList = (props) => {
    return (<div>
        {props.showCart && <Cart />}
        <button onClick={() => props.setShowCart(true)}>cart</button>
        {props.arr.map((item) => {
            return (<Product key={parseInt(item.code)} item={item} />)
        })}
    </div>);
}
const mapStateToProps = (state) => {
    return {
        arr: state.existingAuction.productsList,
        showCart: state.user.showCart
    }
}

export default connect(mapStateToProps, {setShowCart})(ProductList);
