import { connect } from "react-redux";
import React from "react";
class Clock extends React.Component {
    constructor(props) {
        super(props);
        let fixDate = new Date(12, 8, 2021);// עד איזה זמן
        let currDate = new Date();// מה הזמן כעת
        let secs = Math.floor((fixDate - (currDate)) / 1000);
        let mins = Math.floor(seconds / 60);
        let hours = Math.floor(minutes / 60);
        let days = Math.floor(hours / 24);
        this.state = { fixDate, currDate, secs, mins, hours, days };
    }

    tick() {
        //מעדכנת את הסטייט
        this.setState((prevState, props) => ({
            diff: prevState.fixDate - (new Date()).getTime(),
        }));
    }
    componentDidMount() {
        //כשהקומפוננטה נטענת נגדיר את פונ' טיק שתתבצע כל שניה
        this.interval = setInterval(() => this.tick(), 1000);
    }
    componentWillUnmount() {
        //כשהקומפוננטה נסגרת נעצור את פעילות הפונ' טיק
        clearInterval(this.interval);
    }



    render() {
        let { diff } = this.state;
        let { fixDate } = this.state;
        let { currDate } = this.state;
        let { secs } = this.state;
        let { mins } = this.state;
        let { hours } = this.state;
        let { days } = this.state;

        hours = hours - (days * 24);
        minutes = minutes - (days * 24 * 60) - (hours * 60);
        seconds = seconds - (days * 24 * 60 * 60) - (hours * 60 * 60) - (mins * 60);

        if (diff <= 0) {
            clearInterval(this.interval);
            //00:אם יעשו ריענון כשהשעון שלילי נרצה שיציג 00:00:00
            days = 0; hours = 0; mins = 0; secs = 0;
        }
        return (
            <div>
                <h2>{days}:{String(hours).padStart(2, '0')}:{String(mins).padStart(2, '0')}:{String(secs).padStart(2, '0')}</h2>
            </div>
        );
    }

}
const mapStateToProps = (state) => {
    return {
        theDate: state.currentAuction.registrationEndDate
    }
}

export default connect(mapStateToProps, {})(Clock);
