import React, { useState } from 'react';
import './Login.scss';
import SignUp from './SignUp'
import SignIn from './SignIn'


const Login = () => {
  const [isOpen, setIsOpen] = useState(true);
  // const [isOpen, setIsOpen] = useState(false);
  const [show, setShow] = useState(<SignIn />);
  const myRef = React.createRef();


  const openModal = () => {
    setIsOpen(true);
    myRef.current.style.display = "block";
  }
  const closeModal = () => {
    setIsOpen(false);
    myRef.current.style.display = "none";
  }
  const signInShow = (e) => {
    setShow(<SignIn/>);
  } 
   const signUpShow = (e) => {
     setShow(<SignUp/>);
  }
  const click=(e)=>{
    e.stopPropagation();
  }
  return (
    <center>
      {/* <button onClick={openModal}  >Open Modal</button> */}
      <div id="myModal" className="modal glass" onClick={closeModal} ref={myRef} >
        <div className="modal-content"  onClick={click}>
          <div className="btn" id="btn_signin" onClick={signInShow} >SIGN IN</div>
          <div className="btn" id="btn_signup" onClick={ signUpShow}>SIGN UP</div>
          <div id="login_top" >
            {show}
          </div>
          <div id="login_bottom"></div>
          {/* REGISTER */}
        </div>
      </div>
    </center>
  );
}

export default Login;
