import React, { useState } from 'react';
import { addUserToDB } from '../../utils/userUtils';//הוספת משתמש למאגר
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import Button from '@material-ui/core/Button';
import InputAdornment from '@material-ui/core/InputAdornment';
import IconButton from '@material-ui/core/IconButton';
import clsx from 'clsx';
import { makeStyles } from '@material-ui/core/styles';
import FilledInput from '@material-ui/core/FilledInput';
import './User.scss';
import User from '../../models/user'

import Checkbox from '@mui/material/Checkbox';
import FormControlLabel from '@mui/material/FormControlLabel';
import { setCurrentUser } from '../../store/actions/signUp';
import { connect } from "react-redux";
import { useForm } from "react-hook-form";
import { setLogin } from '../../store/actions/home';
const useStyles = makeStyles((theme) => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap'
    },
    margin: {
        margin: theme.spacing(1),
    },
    withoutLabel: {
        marginTop: theme.spacing(3),
    },
    textField: {
        width: '25ch',
    },
    input_pas_ma: {
        color: '#8e8e95',
        height: '4vh !important',
        background: 'none !important',
        color: '#8e8e95 !important',
        width: '70% !important',
        padding: '0vw !important',
        borderBottomColor: '8e8e95 !important',
        background: 'none !important',
        alignSelf: 'flex-end',
        marginRight: 'auto',
        marginLeft: 'auto'

    },
    eye: {
        color: '#8e8e95',

    },
    placeholder_in_form: {
        marginLeft: '3% !important',
        marginBottom: ' -2% !important'
    }
}));

const SignUp = (props) => {

    const classes = useStyles();
    // const handleChangePassword = (prop) => (event) => {
    //     setValues({ ...values, [prop]: event.target.value });
    //     setNewUser(prevState => ({
    //         ...prevState,
    //         ['password']: event.target.value
    //     }))
    // };
    const handleClickShowPassword = () => { setValues({ ...values, showPassword: !values.showPassword }); };
    const handleMouseDownPassword = (event) => { event.preventDefault(); };
    const [values, setValues] = React.useState({
        amount: '',
        password: '',
        weight: '',
        weightRange: '',
        showPassword: false,
    });

    // const [newUser, setNewUser] = useState({ password: "", userName: "", email: "", phone: "", birthYear: "", city: "", confidentiality: false })

    // const handleChange = e => {
    //     const { name, value } = e.target;
    //     setNewUser(prevState => ({
    //         ...prevState,
    //         [name]: value
    //     }))
    // }
    const { register, handleSubmit, formState: { errors } } = useForm();

    let password = "";
    let email = "";
    let name = "";
    let city = "";
    let birthYear = "";
    let phone = "";
    let confidentiality = false;

    let submit = (data, e) => {
        e.preventDefault();

        password = data.password;
        email = data.email;
        name = data.name;
        city = data.city;
        birthYear = data.birthYear;
        phone = data.phone;
        confidentiality = data.confidentiality;
        let addNewUser = new User(password, name, email, phone, birthYear, city, confidentiality)

        console.log(addNewUser)
        addUserToDB(addNewUser).then(succ => {
            if (succ.status != 400) {
                props.setCurrentUser(succ.data);
                props.setLogin(false)
            }
        });
    }
    return (
        <center>
            <form className={classes.root} noValidate autoComplete="off" onSubmit={handleSubmit(submit)}>
                <div className={"inputs_btns"}>

                    <FilledInput
                        name="name"
                        type={'text'}
                        placeholder="Name"
                        required
                        className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
                        variant="filled"
                        // onChange={handleChange}
                        startAdornment={
                            <InputAdornment position="start">
                                <i className="user icon"></i>
                            </InputAdornment>
                        }
                        {...register('name', { required: true })}

                    />
                    <FilledInput
                        name="email"
                        type={'email'}
                        placeholder="Email address"
                        className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
                        variant="filled"
                        required
                        // onChange={handleChange}
                        startAdornment={
                            <InputAdornment position="start">
                                <i className="envelope icon"></i>
                            </InputAdornment>
                        }
                        {...register('email', { required: true })}

                    />
                    <FilledInput
                        name="city"
                        type={'text'}
                        placeholder="city"
                        required
                        className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
                        variant="filled"
                        // onChange={handleChange}
                        startAdornment={
                            <InputAdornment position="start">
                                <i className="map marker alternate icon"></i>
                            </InputAdornment>
                        }
                        {...register('city', { required: true })}

                    />
                    <FilledInput
                        name="birthYear"
                        type={'text'}
                        placeholder="Year Of Birth"
                        required
                        className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
                        variant="filled"
                        // onChange={handleChange}
                        startAdornment={
                            <InputAdornment position="start">
                                <i className="calendar alternate outline icon"></i>
                            </InputAdornment>
                        }
                        {...register('birthYear', { required: true })}

                    />
                    <FilledInput
                        type={'text'}
                        name="phone"
                        placeholder="Phone Number"
                        required
                        className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
                        variant="filled"
                        // onChange={handleChange}
                        startAdornment={
                            <InputAdornment position="start">
                                <i className="mobile alternate icon" />
                            </InputAdornment>
                        }
                        {...register('phone', { required: true })}

                    />
                    <FilledInput
                        name="password"
                        type={values.showPassword ? 'text' : 'password'}
                        placeholder="Password"
                        required
                        className={clsx(classes.margin, classes.textField, classes.input_pas_ma)}
                        variant="filled"
                        // onChange={handleChangePassword('password')}
                        startAdornment={
                            <InputAdornment position="start">
                                <i className="lock icon" />
                            </InputAdornment>
                        }
                        endAdornment={
                            <InputAdornment position="end">
                                <IconButton
                                    onClick={handleClickShowPassword}
                                    onMouseDown={handleMouseDownPassword}
                                    className={classes.eye}
                                >
                                    {values.showPassword ? <Visibility /> : <VisibilityOff />}
                                </IconButton>
                            </InputAdornment>
                        }
                        {...register('password', { required: true })}

                    />
                    <FormControlLabel
                        control=
                        {<Checkbox /*onChange={(e) => setNewUser(prevState => ({
                            ...prevState,
                            ['confidentiality']: e.target.checked
                        }))}*/ />}
                        label="Confidentiality"
                        {...register('confidentiality', { required: false })}

                    />

                    <Button type="submit" variant="contained" className={"login_btn"} >Login</Button>

                    {/* <Button type="button" variant="contained" className={"login_btn"} onClick={createUser}>Login</Button> */}
                </div>

            </form>
        </center>

    );

}

