import './auctionManager.scss'

import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { connect } from "react-redux";
import React, { useEffect, useState, useRef } from 'react';
import defaultLogo from '../../../img/defaultLogo.jpg'
import { getAuctionFromDB } from '../../../store/actions/currentAuction'
import { Link } from 'react-router-dom'
import { getBestSellingProductInAuctionFromDB } from '../../../utils/auctionManagerUtils'
import { getTotalRevenueAllAuctionsFromDB } from '../../../utils/mainUtils';
import { SET_DISAPPROVAL_AUCTION_MODAL } from '../../../store/actionTypes';
const AuctionStatistics = (props) => {

    useEffect(() => {
        if (props.auction) {
            getBestSellingProductInAuctionFromDB(props.auction_id).then(succ => {
                setBestProduct(succ.data);
                console.log(succ.data)
            });
            getTotalRevenueAllAuctionsFromDB(props.auction_id).then(succ => {
                if (succ.status != 400) SET_DISAPPROVAL_AUCTION_MODAL(succ.data);
                console.log(succ.data)
            })
        }
        else window.location = "http://localhost:3000/your_profile"
    }, []);

    const [bestProduct, setBestProduct] = useState(null);
    const [sum, setSum] = useState(0);


    return (
        <>

            <h1 >Chinese auction statistics</h1>

            <Card sx={{ maxWidth: 345 }}>
                <CardMedia
                    component="img"
                    height="140"
                    image={props.logo || defaultLogo}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">{props.title}</Typography>
                    <Typography variant="body2" color="text.secondary">{props.content}</Typography>
                </CardContent>
                {props.btn && <CardActions>
                    <Link
                        onClick={() => props.getAuctionFromDB(props._id)}
                        to={`/auction`}>
                        <Button size="large">Join up</Button>
                    </Link>
                </CardActions>}
            </Card>




            <Card sx={{ maxWidth: 345 }}>
                <CardMedia
                    component="img"
                    height="140"
                    image={props.logo || defaultLogo}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">{props.title}</Typography>
                    <Typography variant="body2" color="text.secondary">{props.content}</Typography>
                </CardContent>
                {props.btn && <CardActions>
                    <Link
                        onClick={() => props.getAuctionFromDB(props._id)}
                        to={`/auction`}>
                        <Button size="large">Join up</Button>
                    </Link>
                </CardActions>}
            </Card>


            <Card sx={{ maxWidth: 345 }}>
                <CardMedia
                    component="img"
                    height="140"
                    image={props.logo || defaultLogo}
                />
                <CardContent>
                    <Typography gutterBottom variant="h5" component="div">{props.title}</Typography>
                    <Typography variant="body2" color="text.secondary">{props.content}</Typography>
                </CardContent>
                {props.btn && <CardActions>
                    <Link
                        onClick={() => props.getAuctionFromDB(props._id)}
                        to={`/auction`}>
                        <Button size="large">Join up</Button>
                    </Link>
                </CardActions>}
            </Card>

        </>
    );

}

const mapStateToProps = (state) => {
    return {
        auction_id: state.auctionManager.selected_auction_to_options._id

    };
}
export default connect(mapStateToProps, {})(AuctionStatistics);
