const AuctionResults = (props) => {

    useEffect(() => {
        let id = localStorage.getItem("user");
        if (id && props.currentUser == null) {

            // let a_id = localStorage.getItem("currentAuction"); let n_a_id = localStorage.getItem("newAuction");
