            // if (n_a_id) props.setNewAuctionByStorage(n_a_id);
            props.setUserByStorage(id);
        }
    }, [])

    useEffect(() => {
        if (props.user)
            getUserOrdersListFromDB(props.user._id).then(succ => {
                if (succ.status != 400) {
                    let arr = [];
                    succ.data.map((o) => { arr.push(createData(o)) });
                    console.log(succ.data);
                    setRows(arr);
                }
            });
    }, [props.currentUser])

    return (
        <>

            <h1>Chinese auction results</h1>
            {productsList.map((p, index) => {
                if (p.winnerId) return <p key={index}>{p.name}:{p.winnerId.confidentiality ? "Anonymous" : p.winnerId.userName}</p>
            })}
        </>
    );

}

const mapStateToProps = (state) => {
    return {
        auction_id: state.auctionManager.selected_auction_to_options

    };
}
export default connect(mapStateToProps, {})(AuctionResults);
