import { connect } from "react-redux";
import { deleteProduct } from "../../../store/actions/newAuction";
import { deleteProductFromDB } from "../../../utils/newAuctionUtils";
import moment from 'moment'
import Switch from '@material-ui/core/Switch';
import IconButton from '@material-ui/core/IconButton';

const useStyles = makeStyles({
    root: { width: '80%', },
    container: { maxHeight: 440, }
});
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import { Link } from 'react-router-dom';
import de from '../../../img/icons/dustbin.png'
import '../NewAuction.scss'
const ProductsList = (props) => {
    useEffect(() => {
        props.productsList && props.productsListmap((o) => { arr.push(createData(o)) });
        setRows(arr);
    }, [])


    const columns = [
        { id: 'name', label: 'Name', minWidth: 80 },
        {
            id: 'description',
            label: 'Description',
            minWidth: 170,
            align: 'right',
            format: (value) => value.toFixed(2)
        },
        {
            id: 'includedInPackages',
            label: 'Included in packages',
            minWidth: 170,
            align: 'right',
            format: (value) => value.toFixed(2),
        },
        {
            id: 'price',
            label: 'Price',
            minWidth: 170,
            align: 'right',
            format: (value) => value.toFixed(2)
        }, {
            id: 'del',
            label: '',
            minWidth: 170,
            align: 'right',
            format: (value) => value.toFixed(2)
        }
    ];

    const createData = (pro) => {
        const del = <IconButton
            onClick={() =>
                deleteProductFromDB(props.auctionId, item._id).then(succ => {
                    if (succ.status != 400) props.deleteProduct(item._id)
                })}
            title="Delete" ><img className="my_icon" src={de} />
        </IconButton>
        return { name: pro.name, description: pro.description, includedInPackages: pro.includedInPackages, price: pro.price, del };
    }

    const [rows, setRows] = useState([]);

    const useStyles = makeStyles({
        root: {
            width: '60%',
        },
        container: {
            maxHeight: 440,
        },
    });
    const style1 = { marginLeft: '36vw', marginTop: '4vh' }

    return (
        <center>
            <h1>UserTable</h1>
            <Paper className={classes.root} style={style1}>
                <TableContainer className={classes.container} >
                    <Table stickyHeader aria-label="sticky table">
                        <TableHead>
                            <TableRow>
                                {columns.map((column) => (
                                    <TableCell
                                        key={column.id}
                                        align={column.align}
                                        style={{ minWidth: column.minWidth }}
                                    >
                                        {column.label}
                                    </TableCell>
                                ))}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {rows && rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                                return (
                                    <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                                        {columns.map((column) => {

                                            const value = row[column.id];
                                            return (
                                                <TableCell key={column.id} align={column.align}>
                                                    {value}
                                                </TableCell>
                                            );
                                        })}
                                    </TableRow>
                                );
                            })}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </center>
    );
}

const myMapStateToProps = (state) => {
    return {
        auctionId: state.auction.newAuction._id,
        productsList: state.auction.newAuction.productList
    }
}
export default connect(myMapStateToProps, { deleteProduct })(ProductsList);
