import { connect } from "react-redux";
import React from "react";
class Clock extends React.Component {
    constructor(props) {
        super(props);
        let fixDate = (new Date()).setHours(15, 17, 0); // עד איזה זמן
        let currDate = new Date();// מה הזמן כעת
        this.state = { fixDate, diff: fixDate - currDate };
    }

    tick() {
        //מעדכנת את הסטייט
        this.setState((prevState, props) => ({
            diff: prevState.fixDate - (new Date()).getTime(),
        }));
    }
    componentDidMount() {
        //כשהקומפוננטה נטענת נגדיר את פונ' טיק שתתבצע כל שניה
        this.interval = setInterval(() => this.tick(), 1000);
    }
    componentWillUnmount() {
        //כשהקומפוננטה נסגרת נעצור את פעילות הפונ' טיק
        clearInterval(this.interval);
    }

    render() {
        let { diff } = this.state;
        let hours = Math.floor(diff / (60 * 60 * 1000));
        let mins = Math.floor((diff - (hours * 60 * 60 * 1000)) / (60 * 1000));
        let secs = Math.floor((diff - (hours * 60 * 60 * 1000) - (mins * 60 * 1000)) / 1000);
        /*if (hours < 0 || (hours == 0 && mins == 0 && secs == 0)) clearInterval(this.interval);
        if (hours < 0) { hours = 0; mins = 0; secs = 0; }*/
           if (diff == 0) clearInterval(this.interval);
        return (
            <div>
                <h2>{hours}:{mins}:{secs}</h2>
            </div>
        );
    }

}
const mapStateToProps = (state) => {
    return {
        //theDate: state.u
    }
}

export default connect(mapStateToProps, {})(Clock);
