import lesson2 from '../../img/terms.pdf'
import React, { Component } from 'react';

import { Document, Page } from 'react-pdf'

class App extends Component {
	state = { numPages: null, pageNumber: 1 };

	onDocumentLoadSuccess = ({ numPages }) => {
		this.setState({ numPages });
	};

	goToPrevPage = () => { if (state.pageNumber > 1) this.setState(state => ({ pageNumber: state.pageNumber - 1 })); }
	goToNextPage = () => { if (state.pageNumber < state.numPages) this.setState(state => ({ pageNumber: state.pageNumber + 1 })); }

	render() {
		const { pageNumber, numPages } = this.state;

		return (
			<div>
				<nav>
					<button onClick={this.goToPrevPage}>Prev</button>
					<button onClick={this.goToNextPage}>Next</button>
				</nav>

				<div style={{ width: 600 }}>
					<Document
						file={lesson2}
						onLoadSuccess={this.onDocumentLoadSuccess}
					>
						<Page pageNumber={pageNumber} width={600} />
					</Document>
				</div>

				<p>
					Page {pageNumber} of {numPages}
				</p>
			</div>
		);
	}
}

export default App;
