import React, { useEffect, useState } from 'react';
import Box from '@mui/material/Box';
import Collapse from '@mui/material/Collapse';
import IconButton from '@mui/material/IconButton';
import Table from '@mui/material/Table';
import TableBody from '@mui/material/TableBody';
import TableCell from '@mui/material/TableCell';
import TableContainer from '@mui/material/TableContainer';
import TableHead from '@mui/material/TableHead';
import TableRow from '@mui/material/TableRow';
import Typography from '@mui/material/Typography';
import Paper from '@mui/material/Paper';
import KeyboardArrowDownIcon from '../../img/icons/arrow-down.png'
import KeyboardArrowUpIcon from '../../img/icons/arrow-up.png'
import { Link } from 'react-router-dom';
import { connect } from "react-redux";
import { setNewAuction } from '../../store/actions/newAuction'
import { getUnapprovedAuctionsByUserFromDB } from '../../utils/auctionManagerUtils';

const createData = (auction) => {
    return {
        /*auctionManager: "611c2f2e18f13934fc07bc27"
lotteryApproval: false
organizationName: "name1"
organizationPhotos: ['']
organizationText: "1eeeeeeeeeeeeee"
productList: (4) [{…}, {…}, {…}, {…}]
publicationApproval: false
purchasePackage: (3) [{…}, {…}, {…}]
status: "DONE"*/
        name: auction.name, productsQty: auction.productList.length, purchasePackages: auction.purchasePackage.length, lotteriesDate: auctoin.lotteriesDate,
        history: [
            {
                date: '2020-01-05',
                customerId: '11091700',
                amount: 3,
            },
            {
                date: '2020-01-02',
                customerId: 'Anonymous',
                amount: 1,
            },
        ],
    };
}

const Row = (props) => {
    const { row } = props;
    const [open, setOpen] = React.useState(false);

    return (
        <React.Fragment >
            <TableRow sx={{ '& > *': { borderBottom: 'unset' } }}>
                <TableCell>
                    <IconButton
                        aria-label="expand row"
                        size="small"
                        onClick={() => setOpen(!open)}
                    >
                        {open ?
                            <img className="view-details-icon" src={KeyboardArrowUpIcon} />
                            : <img className="view-details-icon" src={KeyboardArrowDownIcon} />}
                    </IconButton>
                </TableCell>
                <TableCell component="th" scope="row">
                    {row.name}
                </TableCell>
                <TableCell align="right">{row.calories}</TableCell>
                <TableCell align="right">{row.fat}</TableCell>
                <TableCell align="right">{row.carbs}</TableCell>
                <TableCell align="right">{row.protein}</TableCell>
            </TableRow>
            <TableRow>
                <TableCell style={{ paddingBottom: 0, paddingTop: 0 }} colSpan={6}>
                    <Collapse in={open} timeout="auto" unmountOnExit>
                        <Box sx={{ margin: 1 }}>
                            <Typography variant="h6" gutterBottom component="div">
                                History
                            </Typography>
                            <Table size="small" aria-label="purchases">
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Date</TableCell>
                                        <TableCell>Customer</TableCell>
                                        <TableCell align="right">Amount</TableCell>
                                        <TableCell align="right">Total price ($)</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {row.history.map((historyRow) => (
                                        <TableRow key={historyRow.date}>
                                            <TableCell component="th" scope="row">
                                                {historyRow.date}
                                            </TableCell>
                                            <TableCell>{historyRow.customerId}</TableCell>
                                            <TableCell align="right">{historyRow.amount}</TableCell>
                                            <TableCell align="right">
                                                {Math.round(historyRow.amount * row.price * 100) / 100}
                                            </TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        </Box>
                    </Collapse>
                </TableCell>
            </TableRow>
        </React.Fragment>
    );
}

// const rows = auctionsList && auctionsList.map(a => createData(a));
// [
createData('Frozen yoghurt', 159, 6.0, 24)
// createData('Ice cream sandwich', 237, 9.0, 37, 4.3, 4.99),
// createData('Eclair', 262, 16.0, 24, 6.0, 3.79),
// createData('Cupcake', 305, 3.7, 67, 4.3, 2.5),
// createData('Gingerbread', 356, 16.0, 49, 3.9, 1.5),
// ];

const ContinueNewAuction = (props) => {

    const [auctionsList, setAuctionsList] = useState([]);//המכירות שלו שעדיין לא אושרו לתצוגה
    // const [auctionsListData, setAuctionsListData] = useState([]);//המכירות שלו שעדיין לא אושרו לתצוגה

    useEffect(() => {
        getUnapprovedAuctionsByUserFromDB(props.currentUser._id).then(succ => {
            setAuctionsList(succ.data);
            console.log(auctionsList);
            // auctionsList.map(a=>{})
        });
    }, []);

    return (
        <TableContainer component={Paper} id={"unapproved-auctions-list"}>
            <Table aria-label="collapsible table">
                <TableHead>
                    <TableRow>
                        <TableCell /> {/* האייקון של פתח/סגור */}
                        <TableCell>Chiense auction name</TableCell>
                        <TableCell align="right">Purchase packages</TableCell>
                        <TableCell align="right">Products</TableCell>
                        <TableCell align="right">Lotteries date</TableCell>
                    </TableRow>
                </TableHead>
                <TableBody>
                    {auctionsList.map((a) => {
                        let row = createData(a);
                        debugger;
                        <Row key={row.name} row={row} />
                    })}
                    {/* {rows.map((row) => (
                        <Row key={row.name} row={row} />
                    ))} */}
                </TableBody>
            </Table>
        </TableContainer>
    );
}
//TODO לעשות כאן
const mapStateToProps = (state) => {
    return {
        currentUser: state.user.currentUser
    };
}
export default connect(mapStateToProps, { setNewAuction })(ContinueNewAuction);
