// import React, { useState } from 'react';
import { connect } from "react-redux";
import { Form, Input, TextArea, Button } from 'semantic-ui-react';
import './main.scss';
import Swal from 'sweetalert2';
import emailjs from 'emailjs-com';
import { setShowContactForm } from '../../store/actions/home';
import { sendContact } from '../../utils/userUtils'


import { useForm } from "react-hook-form";
import { TextField } from "@mui/material";
import React from "react";


const ContactForm = () => {

    let submit = (data, e) => {
        e.preventDefault();
        let newProduct = { image: "", name: "", description: "", price: 0, includedInPackages: true };

        newProduct.name = data.name;
        newProduct.description = data.description;
        newProduct.price = parseInt(data.price);
        newProduct.includedInPackages = data.includedInPackages;
        newProduct.image = data.image;

        debugger;
        let details = { name: e.target.name.value, email: e.target.email.value, phone: e.target.phone, message: e.target.message.value, subject: e.target.subject.value }
        sendContact(details);
        // TODO: למחוק את מה שכתוב בטופס
    }

    const { register, handleSubmit, formState: { errors } } = useForm();

    return (
        <form noValidate autoComplete="off" onSubmit={handleSubmit(submit)}>
            <h3 className="mt-4">
                {/* icon  */}
                Write to us:
            </h3>

            <TextField className="txt" variant="standard"  {...register('name', { required: true })} id="input-with-icon-grid" label="Your name" />
            <TextField className="txt" variant="standard"  {...register('email', { required: true })} id="input-with-icon-grid" label="Your email" />
            <TextField className="txt" variant="standard"  {...register('phone', { required: true })} id="input-with-icon-grid" label="Your phone" />
            <TextField className="txt" variant="standard"  {...register('subject', { required: true })} id="input-with-icon-grid" label="Message subject" />
            <TextField className="txt" variant="standard"  {...register('message', { required: true })} id="input-with-icon-grid" label="Your message" />

            {/* <MDBBtn rounded color="blue">
                                            <MDBIcon icon="paper-plane" />
                                        </MDBBtn> */}


            <h3 className="my-4 pb-2">Contact information</h3>


            <p>
                <MDBIcon icon="map-marker-alt" className="pr-2" />
                Israel, Bnei brak
            </p>


            <p>
                {/* icon */}
                +972 315 7271 / +972 316 4376
            </p>


            <p>
                {/* icon */}
                chinese.auctions1@gmail.com
            </p>



            <button type="submit" className="positive ui button">Save</button>
        </form >);
}

export default ContactForm;
