import { connect } from 'react-redux'
import p from '../../img/car.jpg';
import IconButton from '@material-ui/core/IconButton';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import AddShoppingCartIcon from '@material-ui/icons/AddShoppingCart';
import { useState } from 'react';

const Product = () => {
    let price = 10;
    let name = "car";
    let description = "big and nice car. Mitzubishi outlander.";
    let code = "555";
    let image_src = p;
    let  [cnt,setCnt]=useState(0);

    return (<div>
        <h1>{price}</h1>
        {/* הוסף לסל */}
        <IconButton color="primary" aria-label="add to shopping cart">
            <AddShoppingCartIcon />
        </IconButton>
        <img className='prod_img' src={image_src}></img>
        {/* הורד כמות */}
        <IconButton color="primary" onClick={()=>{let c=cnt;c>0? setCnt(c-1):c}}  >-</IconButton>
        {/* הכמות הנוכחית מרותו מוצר */}
        <h2>{cnt}</h2>
        {/* הוסף כמות */}
        <IconButton color="primary" onClick={()=>{let c1=cnt; setCnt(c1+1)}}>+ </IconButton>


    </div>

    )
}
export default connect()(Product)
