import { connect } from "react-redux";
import Auction from './Auction'
import Cart from "./Cart";
import { setShowCart } from '../../store/actions/user'
import { Link, useRouteMatch, BrowserRouter as Router } from 'react-router-dom';

const AuctionList = (props) => {
    return (<div>
        {props.showCart && <Cart />}
        <button onClick={() => props.setShowCart(true)}>cart</button>
        <Router>
            {props.arr.map((item) => {
                return (
                    <Link to={`/auction:${item.code}`}>
                        <Auction key={parseInt(item.code)} item={item} />
                    </Link>)
            })}
        </Router>
    </div>);
}
const mapStateToProps = (state) => {
    return {
        arr: state.main.auctionsList,
        showCart: state.user.showCart
    }
}

export default connect(mapStateToProps, { setShowCart })(AuctionList);
