import axios from 'axios';
import * as actionTypes from '../actionTypes';
import { setCurrentUser } from './signUp'

export const signIn = (password, email) => {
    return (dispatch) => {
        axios.get(`http://localhost:5000/users/signIn/${password}&${email}`).then(succ => {
            console.log(succ.data);
            if (succ.status != 400) {
                let tmp = JSON.parse(localStorage.getItem('user'));
                console.log("tmp", tmp)
                tmp['currentUser'] = succ.data;
                console.log("tmp['currentUser']", tmp['currentUser'])
                console.log("tmp", tmp)
                localStorage.setItem("user", JSON.stringify(tmp));
                console.log(JSON.parse(localStorage.getItem('user')))
                dispatch(setCurrentUser(succ.data),);
            }
        })
    }
}
