const ContactForm = () => {

    const send = (e) => {

    }

    let submit = (data, e) => {
        e.preventDefault();
        let newProduct = { image: "", name: "", description: "", price: 0, includedInPackages: true };

        newProduct.name = data.name;
        newProduct.description = data.description;
        newProduct.price = parseInt(data.price);
        newProduct.includedInPackages = data.includedInPackages;
        newProduct.image = data.image;

        debugger;
        let details = { name: e.target.name.value, email: e.target.email.value, phone: e.target.phone, message: e.target.message.value, subject: e.target.subject.value }
