import React, { Component } from 'react';
import { Document, Page, pdfjs } from "react-pdf";
import lesson2 from '../../img/terms.pdf'

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

class AuctionTerms extends Component {


    state = { numPages: null, pageNumber: 1 };

    onDocumentLoadSuccess = ({ numPages }) => {
        this.setState({ numPages });
    };

    goToPrevPage = () => { if (this.state.pageNumber > 1) this.setState(state => ({ pageNumber: state.pageNumber - 1 })); }
    goToNextPage = () => { if (this.state.pageNumber < this.state.numPages) this.setState(state => ({ pageNumber: state.pageNumber + 1 })); }

    render() {
        const { pageNumber, numPages } = this.state;

        return (
            <div>
                <nav>
                    <button onClick={this.goToPrevPage}>Prev</button>
                    <button onClick={this.goToNextPage}>Next</button>
                </nav>

                <div style={{ width: 600 }}>
                    <Document
                        file={lesson2}
                        onLoadSuccess={this.onDocumentLoadSuccess}
                    >
                        <Page pageNumber={pageNumber} width={600} />
                    </Document>
                </div>

                <p>
                    Page {pageNumber} of {numPages}
                </p>
            </div>
        );
    }
}

export default AuctionTerms;
