import { connect } from "react-redux";
import React, { useEffect, useState } from 'react';
import './Auction.scss'
import SmallFooter from "../main/SmallFooter";
import SmallHeader from "../main/SmallHeader";
import { Document, Page } from 'react-pdf';
import samplePDF from '../../img/terms.pdf';

const AboutAuction = (props) => {

  useEffect(() => {
    window.addEventListener("scroll", changeHeader)
    return () => {
      window.removeEventListener('scroll', changeHeader);
    };
  }, []);
  const changeHeader = () => {
    let s = document.getElementById("small-header");
    if (s != null) {
      let height = 5
      if (document.body.scrollTop > height || document.documentElement.scrollTop > height) {
        if (s != null) s.style.top = "0";
      } else {
        if (s != null) s.style.top = "-500px";
      }
    }
  }
  return (<>
    <SmallHeader />

    <center>
      <h1>About Us</h1>
      <Document file={samplePDF}>
        <Page pageNumber={1} />
      </Document>
      {/* רשימת הפרסים */}
      {/* תאריך התחלה */}
      {/* תאריך סיום */}
      {/* תאריך הגרלות */}
      {/* שם מכירה */}
      {/* שם ארגון */}
      {/* תמונות ארגון */}
      {/* קובץ תקנון */}
      {/* חבילות רכישה */}

    </center>

    <SmallFooter />
  </>);
}

const mapStateToProps = (state) => {
  return {
    loginIsOpen: state.user.loginIsOpen,
    currentUser: state.user.currentUser
  };
}
export default connect(mapStateToProps, {  })(AboutAuction);
