import React from 'react'
import { Button, Header, Icon, Modal } from 'semantic-ui-react'

function SingleProduct() {
  const [open, setOpen] = React.useState(false)

  return (
    <Modal
      closeIcon
      open={open}
      trigger={
       <Card className={classes.root} onClick={()=>{ <SingleProduct/>}}>
                    <Typography gutterBottom variant="h5" component="h2">
                  {price}
                  </Typography>
                <CardMedia  
                  className={classes.media}
                  image={image_src}
                  title="Contemplative Reptile"
                />
            {/* הוסף לסל */}
            <IconButton color="primary" aria-label="add to shopping cart">
                    <AddShoppingCartIcon />
                </IconButton>
                {/* הורד כמות */}
                 <IconButton color="primary" onClick={()=>{let c=cnt;if(cnt>0)setCnt(c-1)}}  >-</IconButton>
               {/* הכמות הנוכחית מרותו מוצר */}
              <h2>{cnt}</h2>
               {/* הוסף כמות */}
             <IconButton color="primary" onClick={()=>{let c=cnt; setCnt(c+1)}}>+ </IconButton> 
          </Card>}
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
    >
      <Header icon='archive' content='Archive Old Messages' />
      <Modal.Content>
        <p>
          Your inbox is getting full, would you like us to enable automatic
          archiving of old messages?
        </p>
      </Modal.Content>
      <Modal.Actions>
        <Button color='red' onClick={() => setOpen(false)}>
          <Icon name='remove' /> No
        </Button>
        <Button color='green' onClick={() => setOpen(false)}>
          <Icon name='checkmark' /> Yes
        </Button>
      </Modal.Actions>
    </Modal>
  )
}

export default SingleProduct
