import axios from 'axios';

import React, { Component } from 'react';

class Img extends Component {

    state = {

        // // בתחילה, לא נבחר קובץ
        selectedFile: null
    };

    //בחר בקובץ (מתוך המוקפץ)
    onFileChange = event => {

        //עדכן את המצב
        this.setState({ selectedFile: event.target.files[0] });

    };

    // בהעלאת קבצים (לחץ על כפתור ההעלאה)
    onFileUpload = () => {

        // צור אובייקט של formData
        const formData = new FormData();

        // עדכן את אובייקט formData
        formData.append(
            "myFile",
            this.state.selectedFile,
            this.state.selectedFile.name
        );

        // פרטי הקובץ שהועלה
        console.log(this.state.selectedFile);

        //// בקשה הוגשה ל- apend backend
        //// שלח אובייקט formData
        axios.post("api/uploadfile", formData);
    };

    // תוכן הקובץ שיוצג לאחר
    // העלאת הקובץ הושלמה
    fileData = () => {

        if (this.state.selectedFile) {

            return (
                <div>
                    <h2>File Details:</h2>

                    <p>File Name: {this.state.selectedFile.name}</p>


                    <p>File Type: {this.state.selectedFile.type}</p>


                    <p>
                        Last Modified:{" "}
                        {this.state.selectedFile.lastModifiedDate.toDateString()}
                    </p>

                </div>
            );
        } else {
            return (
                <div>
                    <br />
                    <h4>Choose before Pressing the Upload button</h4>
                </div>
            );
        }
    };

    render() {

        return (
            <div>
    
                <div>
                    <input type="file" onChange={this.onFileChange} />
                    <button type="file" onClick={this.onFileUpload}>
                        Upload!
				</button>
                </div>
                {this.fileData()}
            </div>
        );
    }
}

export default Img;
