import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import './yourProfile.scss'
import { getUserOrdersList } from '../../utils/userUtils'//מחזירה את ההזמנות של המשתמש
import { getAuctionName } from '../../utils/auctionUtils'//מחזירה את שם המכירה


const UserTable = () => {
    const columns = [
        { id: 'name', label: 'Chinese auction Name', minWidth: 170 },
        { id: 'order_date', label: 'Order Date', minWidth: 100 },
        {
            id: 'sum',//
            label: 'Sum',//מה יהיה רשום
            minWidth: 170,
            align: 'right',
            format: (value) => value.toLocaleString('en-US'),
        },
        {
            id: 'options',
            label: 'Options',
            minWidth: 170,
            align: 'right',
            format: (value) => value.toLocaleString('en-US'),
        },
        // {
        //     id: 'density',
        //     label: 'Density',
        //     minWidth: 170,
        //     align: 'right',
        //     format: (value) => value.toFixed(2),
        // },
    ];

    const createData = (name, order_date, sum) => {
        const options = "to add buttons";
        console.log("create data");
        return { name, order_date, sum, options };
    }

    const [rows, setRows] = useState([]);


    /*[
        createData('Ezer Mizyon', "02/08/2021",150),
        createData('Yad And Shem',"11/08/2021",100),
    ];*/

    const useStyles = makeStyles({
        root: {
            width: '100%',
        },
        container: {
            maxHeight: 440,
        },
    });

    useEffect(() => {
        getUserOrdersList(JSON.parse(localStorage.user).currentUser._id).then(succ => {
            let arr = [];
            let auctionName;
            succ.data.map((item) => {
                getAuctionName(item.auctionId).then(succ => {
                    auctionName = succ.data;
               

                })
     arr.push(createData(auctionName, item.orderDate, item.amountToPay))

                console.log("auctionName");
                console.log(auctionName);

            });
            setRows(arr);
        })
    }, [])

    const classes = useStyles();
    const [page, setPage] = React.useState(0);
    const [rowsPerPage, setRowsPerPage] = React.useState(10);

    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(+event.target.value);
        setPage(0);
    };

    return (
        <center>

            <Paper className={classes.root}>
                <TableContainer className={classes.container}>
                    <Table stickyHeader aria-label="sticky table">
                        <TableHead>
                            <TableRow>
                                {columns.map((column) => (
                                    <TableCell
                                        key={column.id}
                                        align={column.align}
                                        style={{ minWidth: column.minWidth }}
                                    >
                                        {column.label}
                                    </TableCell>
                                ))}
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {rows.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                                return (
                                    <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                                        {columns.map((column) => {
                                            const value = row[column.id];
                                            return (
                                                <TableCell key={column.id} align={column.align}>
                                                    {column.format && typeof value === 'number' ? column.format(value) : value}
                                                </TableCell>
                                            );
                                        })}
                                    </TableRow>
                                );
                            })}
                        </TableBody>
                    </Table>
                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[10, 25, 100]}
                    component="div"
                    count={rows.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>
        </center>
    );
}
const mapStateToProps = (state) => {
    return {

    };
}
export default connect(mapStateToProps, {})(UserTable);
