import { connect } from "react-redux";
import OneAuction from './OneAuction';
import { setCurrentAuction } from '../../store/actions/currentAuction'
import { Link } from 'react-router-dom';
import './home.scss';
import { setLogin } from "../../store/actions/home";
import { getAuctionList } from "../../store/actions/auction";
import { useEffect, useState } from "react";

const AuctionList = (props) => {
    let [auctionList, setAuctionList] = useState([]);

    // //הכנסת רשימה של כל המכירות הקיימות במסד נתונים
    useEffect(() => { setAuctionList(props.getAuctionList()) }, []);


    return (<>
        <h1>nevigbv</h1>
        {auctionList.map((item) => {
            return (
                <Link 
                    key={parseInt(item._id)}
                    onClick={localStorage.getItem("currentUser") ? () => { props.setCurrentAuction(item._id) } : () => { window.scrollTo(0, 0); setLogin(true); }}
                    to={localStorage.getItem("currentUser") ? `/auction` : '#'}>
                    <OneAuction key={parseInt(item._id)} item={item} />
                </Link>
            )
        })}
    </>
    );
}
const mapStateToProps = (state) => {
    return {
        // arr: state.main.auctionsList,
        // currentUser: state.user.currentUser
    }
}

export default connect(mapStateToProps, { setLogin, setCurrentAuction, getAuctionList })(AuctionList);
